UPDATE public."Disease"
SET precaucion_5 = CASE
WHEN name = 'Drug Reaction' THEN 'Leer la información del prospecto: Revisar cuidadosamente el prospecto del medicamento para conocer posibles efectos secundarios y actuar rápidamente en caso de presentarse alguno.'
WHEN name = 'Malaria' THEN 'Usar ropa de manga larga y colores claros: Vestir prendas que cubran la mayor parte del cuerpo para reducir el riesgo de picaduras.'
WHEN name = 'Allergy' THEN 'Llevar siempre epinefrina inyectable: Si tiene alergia grave, portar un autoinyector de epinefrina para emergencias de anafilaxia.'
WHEN name = 'Hypothyroidism' THEN 'Practicar una rutina de sueño regular: Acostarse y despertarse a la misma hora todos los días, incluso los fines de semana, ayuda a regular el reloj biológico y mejora la calidad del sueño, lo cual contribuye a reducir los niveles de estrés.'
WHEN name = 'Psoriasis' THEN 'Seguir el tratamiento médico indicado: Utilizar los medicamentos tópicos o sistémicos prescritos por el dermatólogo para controlar los brotes.'
WHEN name = 'GERD' THEN 'Evitar el alcohol y la cafeína: Limitar o evitar el consumo de bebidas alcohólicas y cafeinadas, ya que pueden relajar el esfínter esofágico inferior y aumentar el reflujo.'
WHEN name = 'Chronic cholestasis' THEN 'Consultar al médico regularmente: Realizar controles periódicos para monitorear la función hepática y ajustar el tratamiento según sea necesario.'
WHEN name = 'hepatitis A' THEN 'Evitar alimentos crudos o mal cocidos: Consumir solo alimentos bien cocidos para reducir el riesgo de infección por el virus de la hepatitis A.'
WHEN name = 'Osteoarthristis' THEN 'Consultar a un fisioterapeuta: Seguir un plan de ejercicios diseñado por un fisioterapeuta para mejorar la flexibilidad y la fuerza de las articulaciones.'
WHEN name = '(vertigo) Paroymsal  Positional Vertigo' THEN 'Mantener buena hidratación: Beber suficiente agua para evitar la deshidratación, ya que esta puede empeorar los síntomas de vértigo.'
WHEN name = 'Hypoglycemia' THEN 'Evitar el consumo excesivo de alcohol sin alimentos: Tomar alcohol con alimentos para reducir el riesgo de hipoglucemia, ya que el alcohol puede interferir con el control del azúcar en sangre.'
WHEN name = 'Acne' THEN 'Evitar la exposición prolongada al sol: Usar protector solar y evitar el sol intenso, ya que algunos tratamientos para el acné pueden aumentar la sensibilidad al sol.'
WHEN name = 'Diabetes' THEN 'Asistir a chequeos médicos frecuentes: Realizar visitas regulares al médico para monitorear la salud general y prevenir complicaciones asociadas a la diabetes.'
WHEN name = 'Impetigo' THEN 'Evitar rascarse las lesiones: No rascarse las llagas para evitar que empeoren o se infecten aún más.'
WHEN name = 'Hypertension' THEN 'Monitorear la presión arterial: Controlar la presión arterial en casa de manera regular para evaluar la efectividad del tratamiento.'
WHEN name = 'Peptic ulcer diseae' THEN 'Dormir con la cabecera elevada: Elevar la cabecera de la cama para evitar el reflujo ácido durante la noche y mejorar los síntomas de la úlcera.'
WHEN name = 'Dimorphic hemorrhoids(piles)' THEN 'Consumir una dieta rica en fibra: Incluir frutas, verduras y granos integrales en la dieta para evitar el estreñimiento y reducir la presión en las hemorroides.'
WHEN name = 'Common Cold' THEN 'Tomar descongestionantes si es necesario: Utilizar medicamentos descongestionantes de venta libre para aliviar la congestión nasal según indicación médica.'
WHEN name = 'Chicken pox' THEN 'Evitar alimentos que puedan irritar las lesiones: No consumir alimentos salados o ácidos que puedan empeorar la irritación de las erupciones de la varicela.'
WHEN name = 'Cervical spondylosis' THEN 'Dormir con una almohada adecuada: Usar una almohada que soporte bien el cuello y mantenga la alineación adecuada durante el descanso.'
WHEN name = 'Hyperthyroidism' THEN 'Evitar el ejercicio intenso: Limitar actividades físicas intensas que puedan agravar los síntomas del hipertiroidismo, como la taquicardia.'
WHEN name = 'Urinary tract infection' THEN 'Mantener una buena higiene: Limpiar la zona genital de adelante hacia atrás para evitar la transferencia de bacterias y prevenir la infección.'
WHEN name = 'Varicose veins' THEN 'Realizar actividad física moderada: Caminar o hacer ejercicios suaves para mejorar la circulación sanguínea y reducir el riesgo de complicaciones.'
WHEN name = 'AIDS' THEN 'Evitar el contacto con sangre infectada: Tomar precauciones en situaciones donde pueda haber contacto con sangre, como primeros auxilios o prácticas médicas.'
WHEN name = 'Paralysis (brain hemorrhage)' THEN 'Participar en programas de rehabilitación: Seguir las recomendaciones del equipo médico para mejorar la calidad de vida y fomentar la recuperación.'
WHEN name = 'Typhoid' THEN 'Evitar alimentos crudos o mal cocidos: Consumir solo alimentos bien cocidos para reducir el riesgo de contagio de la fiebre tifoidea.'
WHEN name = 'Hepatitis B' THEN 'Seguir el tratamiento médico indicado: Tomar los medicamentos recetados y seguir las recomendaciones del médico para evitar complicaciones hepáticas.'
WHEN name = 'Fungal infection' THEN 'Mantener una buena higiene: Lavar las áreas afectadas con agua y jabón suave para prevenir infecciones secundarias.'
WHEN name = 'Hepatitis C' THEN 'Monitorear la función hepática regularmente: Realizar análisis de sangre periódicos para evaluar la función hepática y ajustar el tratamiento según los resultados.'
WHEN name = 'Migraine' THEN 'Identificar y evitar desencadenantes: Mantener un registro de alimentos y actividades para identificar factores que desencadenan las migrañas y evitarlos.'
WHEN name = 'Bronchial Asthma' THEN 'Tomar los medicamentos preventivos según sea necesario: Consultar al médico sobre medicamentos para prevenir ataques graves de asma.'
WHEN name = 'Alcoholic hepatitis' THEN 'Recibir tratamiento nutricional: Trabajar con un nutricionista para diseñar una dieta que apoye la recuperación del hígado y mantenga un buen estado nutricional.'
WHEN name = 'Jaundice' THEN 'Consumir alimentos ricos en antioxidantes: Incluir frutas y verduras ricas en antioxidantes para apoyar la recuperación hepática.'
WHEN name = 'Hepatitis E' THEN 'Evitar el contacto con personas infectadas: Mantener distancia de personas con hepatitis E para reducir el riesgo de contagio.'
WHEN name = 'Dengue' THEN 'Usar ropa de manga larga: Vestir ropa que cubra la piel para evitar las picaduras de mosquitos durante los brotes de dengue.'
WHEN name = 'Hepatitis D' THEN 'Seguir un plan de tratamiento médico: Cumplir con el tratamiento antiviral y las indicaciones del médico para manejar la hepatitis D.'
WHEN name = 'Heart attack' THEN 'Evitar el estrés: Practicar técnicas de relajación como yoga o meditación para reducir el estrés y mejorar la salud cardiovascular.'
WHEN name = 'Pneumonia' THEN 'Mantener una dieta equilibrada: Comer alimentos ricos en nutrientes para ayudar al cuerpo a recuperarse y fortalecer el sistema inmunológico.'
WHEN name = 'Arthritis' THEN 'Tomar medicamentos antiinflamatorios según lo recetado: Usar antiinflamatorios no esteroides para reducir el dolor y la inflamación de la artritis.'
WHEN name = 'Gastroenteritis' THEN 'Lavar las manos con frecuencia: Mantener una buena higiene de manos para evitar la propagación de la infección a otras personas.'
WHEN name = 'Tuberculosis' THEN 'Evitar el contacto con personas vulnerables: No acercarse a personas con sistemas inmunológicos debilitados hasta completar el tratamiento.'
END